function hubspotOnFormReady($form) {
    $form.removeAttr('novalidate');

    if ($form.closest('#contactC,.formC').length == 0) {
        $form.wrap('<div class="formC">');
    }

    $('label', $form).each(function () {
        var text = $.trim($(this).text());
        if (text.length == 0 || text == '*') {
            $(this).remove();
        }
    });

    $('.hs-button', $form)
        .removeAttr('class')
        .addClass('subscribe')
        .before('<br><br>');

    $('.inputs-list input[type="checkbox"]').after('<span class="checkboxDisplay"></span>');
    $('.inputs-list input[type="radio"]').after('<span class="radioDisplay"></span>');

    // add select wrapper to hubspot select menus
    $(".hs-form-field .input").has("select").wrap('<div class="customSelectC standard"><div class="orgSelect"></div></div>');

    $(".customSelectC").each(function () {
        console.log($(this));
        var n = "<div class='currentItem'><\/div>",
          t, i;
        n += "<div class='dropDownItemsC'>";
        n += "<ul>";
        t = "";
        i = "";
        $(this).find("option").each(function () {
            "" == i && (i = $(this).text());
            var r = !1,
              u = !1,
              f = !1;
            $(this).is("[selected]") && (t = $(this).text(), u = !0, r = !0);
            $(this).is("[disabled]") && (f = !0, r = !0);
            n += "<li";
            n = n + " value='" + $(this).val() + "'";
            r && (n += " class='", u && (n += "selected"), f && (n += " disabled"), n += "'");
            n += ">";
            n += $(this).text();
            n += "<\/li>"
        });
        n += "<\/ul>";
        n += "<\/div>";
        $(this).prepend(n);
        "" != t ? $(this).find(".currentItem").text(t) : $(this).find(".currentItem").text(i);
        $(this).on("click", ".currentItem", function (n) {
            n.stopPropagation();
            $(this).parent().hasClass("openSelect") ? ($(this).parent().removeClass("openSelect"), $(this).parent().find(".dropDownItemsC").fadeOut(200)) : ($(".customSelectC .dropDownItemsC").fadeOut(200), $(".customSelectC").removeClass("openSelect"), $(this).parent().addClass("openSelect"), $(this).parent().find(".dropDownItemsC").fadeIn(200))
        });
        $(this).on("click", "li", function (n) {
            var t, i, r;
            n.stopPropagation();
            t = $(this).hasClass("disabled");
            t || (i = $(this).prevAll().length, $(this).parent().parent().parent().find("option:eq(" + i + ")").prop("selected", !0), r = $(this).text(), $(this).parent().parent().parent().find(".currentItem").text(r), $(this).parent().parent().parent().removeClass("openSelect"), $(this).parent().parent().fadeOut(200))
        });
        $(this).change(function () {
            var n = ($(this).find(":selected").val(), $(this).find(":selected").text());
            $(this).find(".currentItem").text(n)
        })
    });
    $("input[type='reset']").click(function () {
        var n = $(this).closest("form");
        $(n).find(".customSelectC").each(function () {
            var n = $(this).find("option:eq(0)").text();
            $(this).find(".currentItem").text(n)
        })
    });
    /ip(hone|od)|ipad/i.test(navigator.userAgent) && $("body").css("cursor", "pointer");
    $("body").on("click", function () {
        $(".customSelectC").removeClass("openSelect");
        $(".customSelectC .dropDownItemsC").fadeOut(200)
    })
}