function hubspotOnFormReady($form) {
    $form.removeAttr('novalidate');

    if ($form.closest('#contactC,.formC').length == 0) {
        $form.wrap('<div class="formC">');
    }

    $('label', $form).each(function () {
        var text = $.trim($(this).text());
        if (text.length == 0 || text == '*') {
            $(this).remove();
        }
    });

    $('.hs-button', $form)
        .removeAttr('class')
        .addClass('subscribe')
        .before('<br><br>');
}